{% extends 'base.html.twig' %}

{% block body %}
    <h1>Dodaj rezultat</h1>
    
    <div class="col-md-6" style="float:left">
        
        <form>
        <input type="integer" id="zawodnikNr" onchange="sendNrLic() "placeholder="Nr Licencji"/>
        <input type="button" value="Ok" onchange="sendNrLic()">
      </form>

      <form enctype="multipart/form-data" action="{{path('findUser')}}" method="post">
        <input type="text" id="find" name="find" placeholder="Wyszukaj osobę" />
       <button type="submit">Szukaj</button>
      </form>


 <p>
      
    {% if (res is defined and res) %}
       <table class="table"> <th>Imie</th> <th>Nazwisko</th> <th>Nr licencji</th> <th>Rok urodzenia</th> <th>Klub</th>
           {% for result in res %}
       <tr>
           <td>{{ result['imie'] }}</td> 
           <td>{{ result['nazwisko'] }}</td> 
           <td>{{ result['nrLic'] }}</td> 
           <td>{{ result['rokU'] }}</td> 
           <td>{{ result['klub'] }}</td> 
           
         <td>
                    <form enctype="multipart/form-data" action="{{path('findUserById')}}" method="post">
                          <input type="hidden" id="findById" name="findById" value="{{ result['id'] }}"/>
                        <button type="submit">X</button></form>
        </td>
           
       </tr><br/>
       {% endfor %}</table>
       
       <select class="form-control"  form="testform" id="testowe" name="testowe" data-width="100%">
        {% for result in res %}
            <option name="selectName{{ result['id'] }}" id="selectId{{ result['id'] }}" value="{{ result['id'] }}">{{ result['imie'] }}</option>
        {% endfor %}
       </select>
       
    {% endif%}
        
    {% if (resById is defined and resById) %}
      <table class="table"> <th>Imie</th> <th>Nazwisko</th> <th>Nr licencji</th> <th>Rok urodzenia</th> <th>Klub</th>
        <tr>
          <td>{{resById.imie}}</td>
          <td>{{resById.nazwisko}}</td>
          <td>{{resById.nrLic}}</td>
          <td>{{resById.rokU}}</td>
          <td>{{resById.klub}}</td>
        </tr>
      </table>
           
           <input type="hidden" id="nazwisko" name="nazwisko" value="{{resById.nazwisko}}"/>
           <input type="hidden" id="nrLic" name="nrLic" value="{{resById.nrLic}}"/>
           <input type="hidden" id="rokU" name="rokU" value="{{resById.rokU}}"/>
           <input type="hidden" id="klub" name="klub" value="{{resById.klub}}"/>
      {% endif%}
 </p>
        
    </div>

   
    <div class="col-md-6" style="float:left">
      
    
        
        <form name="appbundle_rezultaty" method="post">

    <div>
        <div id="appbundle_rezultaty">
            <div>
                <label for="appbundle_rezultaty_konkurencja" class="required">Wybierz konkurencję</label>
                <select id="appbundle_rezultaty_konkurencja" name="appbundle_rezultaty[konkurencja]"><option value="1">Konkurencja1</option>
                    <option value="2">Konkurencja2</option><option value="3">Konkurencja3</option><option value="4">Konkurencja4</option></select></div>
            <div><label for="appbundle_rezultaty_zawodnik" class="required">Zawodnik</label><select id="appbundle_rezultaty_zawodnik" name="appbundle_rezultaty[zawodnik]">
                    <option value="1">Z1Imie</option><option value="2">eef</option><option value="3">213131</option><option value="4">fdadssddddddddddddd</option></select></div>
            <div><label for="appbundle_rezultaty_rezultatS1" class="required">Rezultat s1</label>
                <input type="text" id="appbundle_rezultaty_rezultatS1" name="appbundle_rezultaty[rezultatS1]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_xS1" class="required">X s1</label>
                <input type="text" id="appbundle_rezultaty_xS1" name="appbundle_rezultaty[xS1]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_rezultatS2" class="required">Rezultat s2</label>
                <input type="text" id="appbundle_rezultaty_rezultatS2" name="appbundle_rezultaty[rezultatS2]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_xS2" class="required">X s2</label>
                <input type="text" id="appbundle_rezultaty_xS2" name="appbundle_rezultaty[xS2]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_factor" class="required">Factor</label>
                <input type="text" id="appbundle_rezultaty_factor" name="appbundle_rezultaty[factor]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_uwagiS1" class="required">Uwagi s1</label>
                <textarea id="appbundle_rezultaty_uwagiS1" name="appbundle_rezultaty[uwagiS1]" required="required"></textarea></div>
            <div><label for="appbundle_rezultaty_uwagiS2" class="required">Uwagi s2</label>
                <textarea id="appbundle_rezultaty_uwagiS2" name="appbundle_rezultaty[uwagiS2]" required="required"></textarea></div>
                
                
            <div><label for="appbundle_rezultaty_imie" class="required">Imie</label>
                <input type="text" id="appbundle_rezultaty_imie" name="appbundle_rezultaty[imie]" required="required" maxlength="255" value="{% if (resById is defined and resById) %} {{resById.imie}}   {% endif%}" />
            
            </div>
            
            <div>
                <label for="appbundle_rezultaty_nazwisko" class="required">Nazwisko</label>
                <input type="text" id="appbundle_rezultaty_nazwisko" name="appbundle_rezultaty[nazwisko]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_rokU" class="required">Rok u</label>
                <input type="text" id="appbundle_rezultaty_rokU" name="appbundle_rezultaty[rokU]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_nrLic" class="required">Nr lic</label>
                <input type="text" id="appbundle_rezultaty_nrLic" name="appbundle_rezultaty[nrLic]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_klub" class="required">Klub</label>
                <input type="text" id="appbundle_rezultaty_klub" name="appbundle_rezultaty[klub]" required="required" maxlength="255" /></div>
            <div><label for="appbundle_rezultaty_nazwa_s">Nazwa s</label>
                <input type="text" id="appbundle_rezultaty_nazwa_s" name="appbundle_rezultaty[nazwa_s]" /></div><div><label for="appbundle_rezultaty_nazwa_p">Nazwa p</label>
                <input type="text" id="appbundle_rezultaty_nazwa_p" name="appbundle_rezultaty[nazwa_p]" /></div>
            <input type="hidden" id="appbundle_rezultaty__token" name="appbundle_rezultaty[_token]" value="ZQISOxcwxcyIghRi2KdMI2tO9KNjT6KO3PBPS0ELH90" />

        </div>
    </div>

    <input type="submit" value="Dodaj" />
 
        </form>
        
        
    </div>
    
    
    <script>
function sendNrLic() {
    alert("gdgbfsz");
}
</script>
